import React, { useState } from 'react'; // Importamos React y el hook useState

// Definimos el componente principal App
function App() {
  // Estado para manejar si el submenú está desplegado
  const [isSubMenuOpen, setIsSubMenuOpen] = useState(false);

  // Función para alternar el estado del submenú
  const toggleSubMenu = () => {
    setIsSubMenuOpen(!isSubMenuOpen);
  };

  // Estilos para el menú y el fondo
  const styles = {
    app: {
      backgroundColor: '#f0f4f8', // Color de fondo de la aplicación
      minHeight: '100vh',
      padding: '20px',
      fontFamily: 'Arial, sans-serif',
    },
    menu: {
      backgroundColor: '#333', // Color de fondo del menú
      color: '#fff',
      padding: '10px 20px',
      borderRadius: '5px',
    },
    menuItem: {
      cursor: 'pointer',
      padding: '5px 0',
    },
    subMenu: {
      marginTop: '10px',
      paddingLeft: '20px',
      display: isSubMenuOpen ? 'block' : 'none', // Mostrar u ocultar según el estado
      backgroundColor: '#555',
      padding: '10px',
      borderRadius: '5px',
    },
    subMenuItem: {
      color: '#ccc',
      cursor: 'pointer',
    },
  };

  return (
    <div style={styles.app}>
      {/* Contenedor principal de la aplicación */}
      <nav style={styles.menu}>
        {/* Sección de menú */}
        <div style={styles.menuItem} onClick={() => alert('Navegando a Inicio')}>
          Inicio
        </div>
        <div style={styles.menuItem} onClick={() => alert('Navegando a Sobre Nosotros')}>
          Sobre Nosotros
        </div>
        <div style={styles.menuItem} onClick={toggleSubMenu}>
          {/* Elemento con submenú, al hacer clic se despliega */}
          Quienes Somos
        </div>
        {isSubMenuOpen && (
          <div style={styles.subMenu}>
            {/* Submenú que se despliega */}
            <div style={styles.subMenuItem} onClick={() => alert('Navegando al Blog')}>
              Blog
            </div>
            <div style={styles.subMenuItem} onClick={() => alert('Navegando a Contacto')}>
              Contacto
            </div>
          </div>
        )}
      </nav>
    </div>
  );
}

export default App;
